mapper.insert:
        
            select CAST(ifnull(max(id), 0) AS UNSIGNED) + 1 as id FROM os_cvo
        

        INSERT INTO os_cvo (
            id
            , orchestration_service_id
            , composite_virtual_object_id
        )
        VALUES (
            #{id}
            , #{orchestrationServiceId}
            , #{compositeVirtualObjectId}
        )

INSERT INTO composite_virtual_object (
    id
    , name
    , description
    , aspect_id
    , function_id
    , device_type
    , created_date
    , modified_date
)
VALUES (
    #{id}
    , #{name}
    , #{description}
    , #{aspectId}
    , #{functionId}
    , #{deviceType}
    , now(4)
    , now(4)
)

mapper.select:
SELECT
    id
    , name
    , description
    , aspect_id
    , function_id
    , device_type
    , created_date
    , modified_date
FROM composite_virtual_object

    
        AND id = #{id}
    
    
        AND name = #{name}
    
    
        AND aspect_id = #{aspectId}
    
    
        AND function_id = #{functionId}
    
    
        AND description = #{description}

mapper.update:
UPDATE composite_virtual_object

    modified_date = now(4)
    , name = #{name}
    , description = #{description}
    , aspect_id = #{aspectId}
    , function_id = #{functionId}
    , device_type = #{deviceType}

WHERE
id = #{id}
