mapper.insert:
INSERT INTO rule_body (
      id
    , os_id
    , cvo_id

    , location_id

)
VALUES (
      #{id}
    , #{osId}
    , #{cvoId}

    , #{locationId}

)

INSERT INTO composite_virtual_object (
      id
    , name
    , description
    , created_date
    , modified_date
)
VALUES (
      #{id}
    , #{name}
    , #{description}
    , now(4)
    , now(4)
)

mapper.select:
SELECT
	*
FROM composite_virtual_object


        AND id = #{id}


        AND name = #{name}


        AND description = #{description}

mapper.update:
UPDATE composite_virtual_object

    modified_date = now(4)
    , name = #{name}
    , description = #{description}

WHERE
    id = #{id}
