mapper.insert:
INSERT INTO rule_cvo (
      id
    , os_id
    , cvo_id

    , location_id

)
VALUES (
      #{id}
    , #{osId}
    , #{cvoId}

    , #{locationId}

)

INSERT INTO composite_virtual_object (
      id
    , name
    , description
    , cvo_type
    , device_class_type_id
    , device_id

    , created_date
    , modified_date
)
VALUES (
      #{id}
    , #{name}
    , #{description}
    , #{cvoType}
    , #{deviceClassTypeId}
    , #{deviceId}

    , now(4)
    , now(4)
)

mapper.select:
SELECT
	*
FROM composite_virtual_object


        AND id = #{id}


        AND name = #{name}


        AND description = #{description}


        AND cvo_type = #{cvoType}


        AND device_class_type_id = #{deviceClassTypeId}


        AND device_id = #{deviceId}

mapper.update:
UPDATE composite_virtual_object

    modified_date = now(4)
    , name = #{name}
    , description = #{description}
    , cvo_type = #{cvoType}
    , device_class_type_id = #{deviceClassTypeId}
    , device_id = #{deviceId}

WHERE
    id = #{id}
