    , description
    , created_date
    , modified_date
    , order_cd
)
VALUES (
    #{id}
    , #{name}
    , #{description}
    , now(4)
    , now(4)
    , #{orderCd}
)

mapper.update:
UPDATE orchestration_service

    modified_date = now(4)
    , name = #{name}
    , description = #{description}
    , order_cd = #{orderCd}

WHERE
id = #{id}

