mapper.select:
SELECT *
FROM profile

  
    AND name = #{name}
  
  
    AND description = #{description}
  
  
    AND context_model_id = #{contextModelId}
  
  
    AND location_uri = #{locationUri}
  
  
    AND orchestration_service_id = #{orchestrationServiceId}
  
  
    AND period = #{period}

mapper.insert:
INSERT INTO profile (
  id
  , name
  , description
  , context_model_id
  , orchestration_service_id
  , location_uri
  , enabled
  , period
  , created_date
  , modified_date
  , user_id
)
VALUES (
  #{id}
  , #{name}
  , #{description}
  , #{contextModelId}
  , #{orchestrationServiceId}
  , #{locationId}
  , #{enabled}
  , #{period}
  , now(4)
  , now(4)
  , #{userId}
)

mapper.update:
UPDATE profile SET
  modified_date = now(4)
  , name = #{name}
  , description = #{description}
  , context_model_id = #{contextModelId}
  , orchestration_service_id = #{orchestrationServiceId}
  , location_uri = #{locationUri}
  , enabled = #{enabled}
  , period = #{period}
  , user_id = #{userId}
WHERE
  id = #{id}

UPDATE profile SET
  modified_date = now(4)
  , period = #{period}
WHERE id = #{id}

UPDATE profile SET
  modified_date = now(4)
  , enabled = #{enabled}
WHERE id = #{id}

UPDATE profile SET
  modified_date = now(4)
  , enabled = #{enabled}
