mapper.insert:
INSERT INTO session_data (
  id
  , create_date
  , CONTEXTMODEL_KEY
  , CONTEXTMODEL_NAME
  , CONTEXTMODEL_RESULT
)
VALUES (
  #{id}
  , now(4)
  , #{contextmodelKey}
  , #{contextmodelName}
  , #{contextmodelResult}
)

INSERT INTO session_data_device (
  id
  , DEVICE_KEY
  , DEVICE_LOCATION
  )
VALUES (
  #{id}
  , #{deviceKey}
  , #{deviceLocation}
  )

INSERT INTO session_data_location (
id
, LOCATION_ID
)
VALUES (
#{id}
, #{locationId}
)

INSERT INTO session_data_vo (
id
, VIRTUALOBJECT_KEY
)
VALUES (
#{id}
, #{virtualobjectKey}
)

mapper.select:
SELECT DEVICE_KEY, DEVICE_LOCATION
FROM session_data_device
WHERE id = #{value}

SELECT LOCATION_ID
FROM session_data_location
WHERE id = #{value}

SELECT VIRTUALOBJECT_KEY
FROM session_data_vo
WHERE id = #{value}

mapper.update:
UPDATE session_data

    create_date = now(4)
            , PRIORITY_KEY =         #{priorityKey}
            , PROFILE_NAME =         #{profileName}
             , PROFILE_KEY =          #{profileKey}
        , CONTEXTMODEL_KEY =     #{contextmodelKey}
       , CONTEXTMODEL_NAME =    #{contextmodelName}
     , CONTEXTMODEL_RESULT =  #{contextmodelResult}
     , SERVICEMODEL_RESULT =  #{servicemodelResult}
          , SERVICE_RESULT =       #{serviceResult}
    , VIRTUALOBJECT_RESULT = #{virtualobjectResult}
           , DEVICE_RESULT =        #{deviceResult}

WHERE
  id = #{id}
