mapper.insert:
        
            select CAST(ifnull(max(seq), 0) AS UNSIGNED) + 1 as seq FROM tracking WHERE session_id = #{sessionId}
        

        INSERT INTO tracking (
            session_id
            , seq
            , method
            , uri
            , remote_addr
            , remote_host
            , command_id
            , simulator_type
            , created_time
            , process
            , process_method
            , process_id
            , process_name
            , process_value
            , process_result
            , user_id
            , status_cd
        )
        VALUES (
            #{sessionId}
            , #{seq}
            , #{method}
            , #{uri}
            , #{remoteAddr}
            , #{remoteHost}
            , #{commandId}
            , #{simulatorType}
            , now(4)
            , #{process}
            , #{processMethod}
            , #{processId}
            , #{processName}
            , #{processValue}
            , SUBSTRING(#{processResult}, 1, 1000)
            , #{userId}
            
                
                    , 'R'
                
                
                    , #{statusCd}
                
            
        )
